contracts/GlobalIdentityRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title GlobalIdentityRegistry
 * @dev Core smart contract for managing global digital identities
 * @notice This contract stores cryptographic proofs of identity while preserving privacy
 */
contract GlobalIdentityRegistry is AccessControl, ReentrancyGuard, Pausable {

    // Role definitions
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant VERIFIER_ROLE = keccak256("VERIFIER_ROLE");
    bytes32 public constant ISSUER_ROLE = keccak256("ISSUER_ROLE");

    // Identity structure
    struct Identity {
        bytes32 identityHash;        // Hash of biometric + personal data
        address owner;               // Ethereum address controlling this identity
        uint256 createdAt;          // Timestamp of creation
        uint256 lastUpdated;        // Timestamp of last update
        bool isActive;              // Identity status
        bool isRevoked;             // Revocation status
        string ipfsHash;            // IPFS hash for encrypted metadata
        uint256 verificationLevel;  // 0=unverified, 1=basic, 2=government, 3=biometric
    }

    // Verification attestation
    struct Verification {
        address verifier;           // Who verified this identity
        uint256 timestamp;          // When verification occurred
        uint8 verificationType;     // Type of verification (1=email, 2=phone, 3=gov_id, 4=biometric)
        bytes32 proofHash;          // Hash of verification proof
        bool isValid;               // Current validity status
    }

    // Mapping from identity DID to Identity struct
    mapping(bytes32 => Identity) public identities;

    // Mapping from owner address to identity DID
    mapping(address => bytes32) public addressToIdentity;

    // Mapping from identity DID to verifications
    mapping(bytes32 => Verification[]) public verifications;

    // Mapping to track if an identity hash is already used
    mapping(bytes32 => bool) public identityHashExists;

    // Recovery mechanism - guardian addresses
    mapping(bytes32 => address[]) public guardians;

    // Social recovery - pending recovery requests
    mapping(bytes32 => mapping(address => bool)) public recoveryApprovals;
    mapping(bytes32 => address) public pendingRecoveryAddress;
    mapping(bytes32 => uint256) public recoveryApprovalCount;

    // Statistics
    uint256 public totalIdentities;
    uint256 public totalVerifications;

    // Events
    event IdentityCreated(bytes32 indexed did, address indexed owner, uint256 timestamp);
    event IdentityUpdated(bytes32 indexed did, uint256 timestamp);
    event IdentityRevoked(bytes32 indexed did, uint256 timestamp);
    event IdentityRecovered(bytes32 indexed did, address oldOwner, address newOwner);
    event VerificationAdded(bytes32 indexed did, address indexed verifier, uint8 verificationType);
    event VerificationRevoked(bytes32 indexed did, uint256 verificationIndex);
    event GuardianAdded(bytes32 indexed did, address indexed guardian);
    event GuardianRemoved(bytes32 indexed did, address indexed guardian);
    event RecoveryInitiated(bytes32 indexed did, address indexed newOwner);

    /**
     * @dev Constructor sets up roles
     */
    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
    }

    /**
     * @dev Create a new digital identity
     * @param _identityHash Cryptographic hash of identity data
     * @param _ipfsHash IPFS hash containing encrypted metadata
     */
    function createIdentity(
        bytes32 _identityHash,
        string memory _ipfsHash
    ) external whenNotPaused nonReentrant returns (bytes32) {
        require(!identityHashExists[_identityHash], "Identity already exists");
        require(addressToIdentity[msg.sender] == bytes32(0), "Address already has identity");

        // Generate DID (Decentralized Identifier)
        bytes32 did = keccak256(abi.encodePacked(_identityHash, msg.sender, block.timestamp));

        // Create identity
        identities[did] = Identity({
            identityHash: _identityHash,
            owner: msg.sender,
            createdAt: block.timestamp,
            lastUpdated: block.timestamp,
            isActive: true,
            isRevoked: false,
            ipfsHash: _ipfsHash,
            verificationLevel: 0
        });

        identityHashExists[_identityHash] = true;
        addressToIdentity[msg.sender] = did;
        totalIdentities++;

        emit IdentityCreated(did, msg.sender, block.timestamp);

        return did;
    }

    /**
     * @dev Update identity metadata
     * @param _did Decentralized identifier
     * @param _newIpfsHash New IPFS hash for updated metadata
     */
    function updateIdentity(
        bytes32 _did,
        string memory _newIpfsHash
    ) external whenNotPaused {
        require(identities[_did].owner == msg.sender, "Not identity owner");
        require(identities[_did].isActive, "Identity not active");
        require(!identities[_did].isRevoked, "Identity revoked");

        identities[_did].ipfsHash = _newIpfsHash;
        identities[_did].lastUpdated = block.timestamp;

        emit IdentityUpdated(_did, block.timestamp);
    }

    /**
     * @dev Add verification attestation to an identity
     * @param _did Decentralized identifier
     * @param _verificationType Type of verification
     * @param _proofHash Hash of verification proof
     */
    function addVerification(
        bytes32 _did,
        uint8 _verificationType,
        bytes32 _proofHash
    ) external whenNotPaused onlyRole(VERIFIER_ROLE) {
        require(identities[_did].isActive, "Identity not active");
        require(!identities[_did].isRevoked, "Identity revoked");
        require(_verificationType >= 1 && _verificationType <= 4, "Invalid verification type");

        verifications[_did].push(Verification({
            verifier: msg.sender,
            timestamp: block.timestamp,
            verificationType: _verificationType,
            proofHash: _proofHash,
            isValid: true
        }));

        // Update verification level
        if (_verificationType > identities[_did].verificationLevel) {
            identities[_did].verificationLevel = _verificationType;
        }

        totalVerifications++;

        emit VerificationAdded(_did, msg.sender, _verificationType);
    }

    /**
     * @dev Revoke a specific verification
     * @param _did Decentralized identifier
     * @param _verificationIndex Index of verification to revoke
     */
    function revokeVerification(
        bytes32 _did,
        uint256 _verificationIndex
    ) external onlyRole(VERIFIER_ROLE) {
        require(_verificationIndex < verifications[_did].length, "Invalid index");
        require(
            verifications[_did][_verificationIndex].verifier == msg.sender,
            "Not verification owner"
        );

        verifications[_did][_verificationIndex].isValid = false;

        emit VerificationRevoked(_did, _verificationIndex);
    }

    /**
     * @dev Revoke entire identity (emergency use only)
     * @param _did Decentralized identifier
     */
    function revokeIdentity(bytes32 _did) external {
        require(
            identities[_did].owner == msg.sender || hasRole(ADMIN_ROLE, msg.sender),
            "Not authorized"
        );
        require(!identities[_did].isRevoked, "Already revoked");

        identities[_did].isRevoked = true;
        identities[_did].isActive = false;
        identities[_did].lastUpdated = block.timestamp;

        emit IdentityRevoked(_did, block.timestamp);
    }

    /**
     * @dev Add a guardian for social recovery
     * @param _did Decentralized identifier
     * @param _guardian Guardian address
     */
    function addGuardian(bytes32 _did, address _guardian) external {
        require(identities[_did].owner == msg.sender, "Not identity owner");
        require(_guardian != address(0), "Invalid guardian address");
        require(_guardian != msg.sender, "Cannot be self");

        guardians[_did].push(_guardian);

        emit GuardianAdded(_did, _guardian);
    }

    /**
     * @dev Initiate recovery process
     * @param _did Decentralized identifier
     * @param _newOwner New owner address after recovery
     */
    function initiateRecovery(
        bytes32 _did,
        address _newOwner
    ) external {
        require(_isGuardian(_did, msg.sender), "Not a guardian");
        require(_newOwner != address(0), "Invalid new owner");
        require(pendingRecoveryAddress[_did] == address(0), "Recovery already pending");

        pendingRecoveryAddress[_did] = _newOwner;
        recoveryApprovals[_did][msg.sender] = true;
        recoveryApprovalCount[_did] = 1;

        emit RecoveryInitiated(_did, _newOwner);
    }

    /**
     * @dev Approve pending recovery
     * @param _did Decentralized identifier
     */
    function approveRecovery(bytes32 _did) external {
        require(_isGuardian(_did, msg.sender), "Not a guardian");
        require(pendingRecoveryAddress[_did] != address(0), "No pending recovery");
        require(!recoveryApprovals[_did][msg.sender], "Already approved");

        recoveryApprovals[_did][msg.sender] = true;
        recoveryApprovalCount[_did]++;

        // If majority of guardians approved, execute recovery
        uint256 requiredApprovals = (guardians[_did].length / 2) + 1;
        if (recoveryApprovalCount[_did] >= requiredApprovals) {
            _executeRecovery(_did);
        }
    }

    /**
     * @dev Execute recovery after sufficient approvals
     * @param _did Decentralized identifier
     */
    function _executeRecovery(bytes32 _did) internal {
        address oldOwner = identities[_did].owner;
        address newOwner = pendingRecoveryAddress[_did];

        // Update ownership
        identities[_did].owner = newOwner;
        identities[_did].lastUpdated = block.timestamp;

        // Update address mapping
        delete addressToIdentity[oldOwner];
        addressToIdentity[newOwner] = _did;

        // Clear recovery state
        delete pendingRecoveryAddress[_did];
        recoveryApprovalCount[_did] = 0;

        // Clear all guardian approvals
        for (uint256 i = 0; i < guardians[_did].length; i++) {
            delete recoveryApprovals[_did][guardians[_did][i]];
        }

        emit IdentityRecovered(_did, oldOwner, newOwner);
    }

    /**
     * @dev Check if address is a guardian
     * @param _did Decentralized identifier
     * @param _address Address to check
     */
    function _isGuardian(bytes32 _did, address _address) internal view returns (bool) {
        for (uint256 i = 0; i < guardians[_did].length; i++) {
            if (guardians[_did][i] == _address) {
                return true;
            }
        }
        return false;
    }

    /**
     * @dev Verify identity ownership
     * @param _did Decentralized identifier
     * @param _owner Address to verify
     */
    function verifyOwnership(bytes32 _did, address _owner) external view returns (bool) {
        return identities[_did].owner == _owner && 
               identities[_did].isActive && 
               !identities[_did].isRevoked;
    }

    /**
     * @dev Get identity details
     * @param _did Decentralized identifier
     */
    function getIdentity(bytes32 _did) external view returns (
        address owner,
        uint256 createdAt,
        uint256 lastUpdated,
        bool isActive,
        bool isRevoked,
        string memory ipfsHash,
        uint256 verificationLevel
    ) {
        Identity memory identity = identities[_did];
        return (
            identity.owner,
            identity.createdAt,
            identity.lastUpdated,
            identity.isActive,
            identity.isRevoked,
            identity.ipfsHash,
            identity.verificationLevel
        );
    }

    /**
     * @dev Get verification count for identity
     * @param _did Decentralized identifier
     */
    function getVerificationCount(bytes32 _did) external view returns (uint256) {
        return verifications[_did].length;
    }

    /**
     * @dev Get specific verification
     * @param _did Decentralized identifier
     * @param _index Verification index
     */
    function getVerification(bytes32 _did, uint256 _index) external view returns (
        address verifier,
        uint256 timestamp,
        uint8 verificationType,
        bytes32 proofHash,
        bool isValid
    ) {
        require(_index < verifications[_did].length, "Invalid index");
        Verification memory verification = verifications[_did][_index];
        return (
            verification.verifier,
            verification.timestamp,
            verification.verificationType,
            verification.proofHash,
            verification.isValid
        );
    }

    /**
     * @dev Get guardians for identity
     * @param _did Decentralized identifier
     */
    function getGuardians(bytes32 _did) external view returns (address[] memory) {
        return guardians[_did];
    }

    /**
     * @dev Emergency pause
     */
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    /**
     * @dev Unpause
     */
    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }
}

contracts/PlatformVerificationRegistry.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title PlatformVerificationRegistry
 * @dev Manages verification relationships between identities and platforms (social media, services)
 * @notice This contract enables platforms to verify user identities and manage access control
 */
contract PlatformVerificationRegistry is AccessControl, ReentrancyGuard {

    bytes32 public constant PLATFORM_ADMIN_ROLE = keccak256("PLATFORM_ADMIN_ROLE");

    // Platform registration
    struct Platform {
        string name;
        string domain;
        address adminAddress;
        bool isActive;
        uint256 registeredAt;
        uint256 totalVerifications;
        PlatformType platformType;
    }

    enum PlatformType {
        SocialMedia,
        Financial,
        Healthcare,
        Government,
        Education,
        Other
    }

    // Platform verification record
    struct PlatformVerification {
        bytes32 identityDID;
        bytes32 platformId;
        string platformUserId;      // Platform-specific user ID
        uint256 verifiedAt;
        uint256 expiresAt;
        bool isActive;
        uint8 trustScore;          // 0-100 trust score
        bytes32 attestationHash;   // Hash of verification proof
    }

    // Mapping from platform ID to Platform
    mapping(bytes32 => Platform) public platforms;

    // Mapping from domain to platform ID
    mapping(string => bytes32) public domainToPlatform;

    // Mapping from identity DID to platform verifications
    mapping(bytes32 => mapping(bytes32 => PlatformVerification)) public verifications;

    // Mapping from platform to list of verified identities
    mapping(bytes32 => bytes32[]) public platformVerifiedIdentities;

    // Reverse lookup: platform user ID to identity DID
    mapping(bytes32 => mapping(string => bytes32)) public platformUserToIdentity;

    // Statistics
    uint256 public totalPlatforms;
    uint256 public totalPlatformVerifications;

    // Events
    event PlatformRegistered(bytes32 indexed platformId, string name, string domain);
    event PlatformUpdated(bytes32 indexed platformId);
    event PlatformDeactivated(bytes32 indexed platformId);
    event VerificationCreated(bytes32 indexed identityDID, bytes32 indexed platformId, string platformUserId);
    event VerificationRevoked(bytes32 indexed identityDID, bytes32 indexed platformId);
    event VerificationExpired(bytes32 indexed identityDID, bytes32 indexed platformId);
    event TrustScoreUpdated(bytes32 indexed identityDID, bytes32 indexed platformId, uint8 newScore);

    /**
     * @dev Constructor
     */
    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(PLATFORM_ADMIN_ROLE, msg.sender);
    }

    /**
     * @dev Register a new platform
     * @param _name Platform name
     * @param _domain Platform domain
     * @param _platformType Type of platform
     */
    function registerPlatform(
        string memory _name,
        string memory _domain,
        PlatformType _platformType
    ) external onlyRole(PLATFORM_ADMIN_ROLE) returns (bytes32) {
        require(bytes(_name).length > 0, "Name cannot be empty");
        require(bytes(_domain).length > 0, "Domain cannot be empty");
        require(domainToPlatform[_domain] == bytes32(0), "Domain already registered");

        bytes32 platformId = keccak256(abi.encodePacked(_name, _domain, block.timestamp));

        platforms[platformId] = Platform({
            name: _name,
            domain: _domain,
            adminAddress: msg.sender,
            isActive: true,
            registeredAt: block.timestamp,
            totalVerifications: 0,
            platformType: _platformType
        });

        domainToPlatform[_domain] = platformId;
        totalPlatforms++;

        emit PlatformRegistered(platformId, _name, _domain);

        return platformId;
    }

    /**
     * @dev Create verification between identity and platform
     * @param _identityDID User's decentralized identifier
     * @param _platformId Platform identifier
     * @param _platformUserId Platform-specific user ID
     * @param _expirationDays Days until verification expires (0 = no expiration)
     * @param _attestationHash Hash of verification proof
     */
    function createVerification(
        bytes32 _identityDID,
        bytes32 _platformId,
        string memory _platformUserId,
        uint256 _expirationDays,
        bytes32 _attestationHash
    ) external nonReentrant {
        require(platforms[_platformId].isActive, "Platform not active");
        require(
            platforms[_platformId].adminAddress == msg.sender || 
            hasRole(PLATFORM_ADMIN_ROLE, msg.sender),
            "Not authorized"
        );
        require(bytes(_platformUserId).length > 0, "Platform user ID required");
        require(_identityDID != bytes32(0), "Invalid identity DID");

        // Check if verification already exists
        require(
            !verifications[_identityDID][_platformId].isActive,
            "Verification already exists"
        );

        uint256 expiresAt = _expirationDays > 0 
            ? block.timestamp + (_expirationDays * 1 days)
            : 0;

        verifications[_identityDID][_platformId] = PlatformVerification({
            identityDID: _identityDID,
            platformId: _platformId,
            platformUserId: _platformUserId,
            verifiedAt: block.timestamp,
            expiresAt: expiresAt,
            isActive: true,
            trustScore: 50, // Default middle score
            attestationHash: _attestationHash
        });

        // Update mappings
        platformVerifiedIdentities[_platformId].push(_identityDID);
        platformUserToIdentity[_platformId][_platformUserId] = _identityDID;

        // Update statistics
        platforms[_platformId].totalVerifications++;
        totalPlatformVerifications++;

        emit VerificationCreated(_identityDID, _platformId, _platformUserId);
    }

    /**
     * @dev Revoke verification
     * @param _identityDID User's decentralized identifier
     * @param _platformId Platform identifier
     */
    function revokeVerification(
        bytes32 _identityDID,
        bytes32 _platformId
    ) external {
        require(
            platforms[_platformId].adminAddress == msg.sender || 
            hasRole(PLATFORM_ADMIN_ROLE, msg.sender),
            "Not authorized"
        );
        require(verifications[_identityDID][_platformId].isActive, "Verification not active");

        verifications[_identityDID][_platformId].isActive = false;

        emit VerificationRevoked(_identityDID, _platformId);
    }

    /**
     * @dev Update trust score for a verification
     * @param _identityDID User's decentralized identifier
     * @param _platformId Platform identifier
     * @param _newScore New trust score (0-100)
     */
    function updateTrustScore(
        bytes32 _identityDID,
        bytes32 _platformId,
        uint8 _newScore
    ) external {
        require(
            platforms[_platformId].adminAddress == msg.sender || 
            hasRole(PLATFORM_ADMIN_ROLE, msg.sender),
            "Not authorized"
        );
        require(_newScore <= 100, "Score must be 0-100");
        require(verifications[_identityDID][_platformId].isActive, "Verification not active");

        verifications[_identityDID][_platformId].trustScore = _newScore;

        emit TrustScoreUpdated(_identityDID, _platformId, _newScore);
    }

    /**
     * @dev Check if verification is valid and not expired
     * @param _identityDID User's decentralized identifier
     * @param _platformId Platform identifier
     */
    function isVerificationValid(
        bytes32 _identityDID,
        bytes32 _platformId
    ) external view returns (bool) {
        PlatformVerification memory verification = verifications[_identityDID][_platformId];

        if (!verification.isActive) {
            return false;
        }

        if (verification.expiresAt > 0 && block.timestamp > verification.expiresAt) {
            return false;
        }

        return true;
    }

    /**
     * @dev Get identity DID from platform user ID
     * @param _platformId Platform identifier
     * @param _platformUserId Platform-specific user ID
     */
    function getIdentityByPlatformUser(
        bytes32 _platformId,
        string memory _platformUserId
    ) external view returns (bytes32) {
        return platformUserToIdentity[_platformId][_platformUserId];
    }

    /**
     * @dev Get verification details
     * @param _identityDID User's decentralized identifier
     * @param _platformId Platform identifier
     */
    function getVerification(
        bytes32 _identityDID,
        bytes32 _platformId
    ) external view returns (
        string memory platformUserId,
        uint256 verifiedAt,
        uint256 expiresAt,
        bool isActive,
        uint8 trustScore,
        bytes32 attestationHash
    ) {
        PlatformVerification memory verification = verifications[_identityDID][_platformId];
        return (
            verification.platformUserId,
            verification.verifiedAt,
            verification.expiresAt,
            verification.isActive,
            verification.trustScore,
            verification.attestationHash
        );
    }

    /**
     * @dev Get platform details
     * @param _platformId Platform identifier
     */
    function getPlatform(bytes32 _platformId) external view returns (
        string memory name,
        string memory domain,
        address adminAddress,
        bool isActive,
        uint256 registeredAt,
        uint256 totalVerifications,
        PlatformType platformType
    ) {
        Platform memory platform = platforms[_platformId];
        return (
            platform.name,
            platform.domain,
            platform.adminAddress,
            platform.isActive,
            platform.registeredAt,
            platform.totalVerifications,
            platform.platformType
        );
    }

    /**
     * @dev Get all verified identities for a platform
     * @param _platformId Platform identifier
     */
    function getPlatformVerifiedIdentities(bytes32 _platformId) external view returns (bytes32[] memory) {
        return platformVerifiedIdentities[_platformId];
    }

    /**
     * @dev Deactivate platform
     * @param _platformId Platform identifier
     */
    function deactivatePlatform(bytes32 _platformId) external {
        require(
            platforms[_platformId].adminAddress == msg.sender || 
            hasRole(PLATFORM_ADMIN_ROLE, msg.sender),
            "Not authorized"
        );

        platforms[_platformId].isActive = false;

        emit PlatformDeactivated(_platformId);
    }

    /**
     * @dev Batch verification check for multiple identities
     * @param _identityDIDs Array of identity DIDs
     * @param _platformId Platform identifier
     */
    function batchVerificationCheck(
        bytes32[] memory _identityDIDs,
        bytes32 _platformId
    ) external view returns (bool[] memory) {
        bool[] memory results = new bool[](_identityDIDs.length);

        for (uint256 i = 0; i < _identityDIDs.length; i++) {
            PlatformVerification memory verification = verifications[_identityDIDs[i]][_platformId];

            bool isValid = verification.isActive;
            if (verification.expiresAt > 0 && block.timestamp > verification.expiresAt) {
                isValid = false;
            }

            results[i] = isValid;
        }

        return results;
    }
}

